function tupper_formula(y, x)
    return 1 รท 2 < floor(rem(floor(y รท 17) รท 2^((17 * x) + rem(y, 17)), 2))
end

function plot_tupper(k)
    width = 106
    height = 17
    img = zeros(Bool, height, width)

    for y in 0:(height-1)
        for x in 0:(width-1)
            img[y+1, x+1] = tupper_formula(k + y, x)
        end
    end


    return heatmap(img, aspect_ratio=1,c=:grays, flip=false)
end


